INSERT INTO books (title, author, isbn, image_url, description) VALUES('Heroism in the Harry Potter Series', 'Dr Katrin Berndt', 'ISBN_13 : 9781409478416', 'http://books.google.com/books/content?id=o-QCOFDHmPEC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api','Taking up the various conceptions of heroism that are conjured in the Harry Potter series, this collection examines the ways fictional heroism in the twenty-first century challenges the idealized forms of a somewhat simplistic masculinity associated with genres like the epic, romance and classic adventure story.'); 
INSERT INTO books (title, author, isbn, image_url, description) VALUES ('Field Guide to Harry Potter', 'Colin Duriez', 'ISBN_13 : 9780830834303', 'http://books.google.com/books/content?id=WZwZWP5Sl3AC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'A companion to the Harry Potter books features an overview of the life of J. K. Rowling; discusses the themes presented in the books; offers an A-to-Z of beings, places, things, and events; and includes plot summaries of all seven books.');